mapper.select:
SELECT su.user_name
FROM `sys_user` su
WHERE su.id = #{userId};

SELECT su.id
FROM sys_user su
WHERE su.user_name = #{userName};

SELECT su.user_status
FROM sys_user su
WHERE su.id = #{userId};

SELECT COUNT(su.user_email)
FROM sys_user su
WHERE su.user_email = #{email};

mapper.update:
UPDATE sys_user su
SET su.user_avatar = #{avatarUrl}
WHERE su.id = #{userId};

mapper.select:
SELECT *
FROM sys_user su
WHERE su.id = #{userId};
